// data:image/gif;base64," alt="">\
				
window.CommFunc = (function($){
    var comm = {
		baseUrl: '/',
		fShowLoading: function(){
			var loadBox = $('#__loadBox');
            var clientHeight = document.documentElement.clientHeight;
            if (loadBox.length <= 0) {
				$('<div class="loading" id="__loadBox" style="height:' + clientHeight + 'px;">\
				<div class="loading_popbg"></div><div class="loading_popct">\
				<img src="../assets/images/loadding.gif"></div></div>').appendTo($('body'));
                loadBox = $('#__loadBox');
            }
            loadBox.show();
		},
		fHideLoading: function(){
			$('#__loadBox').remove();
		},
        fAjax: function(options){
			var _default = {
				beforeSend: function(){
					options.needLoading && comm.fShowLoading();
				},
				contentType: "application/x-www-form-urlencoded",
				// contentType: "application/json;charset=UTF-8",
				// dataType : 'JSON',
				complete: function(){
					options.needLoading && comm.fHideLoading();
				}

			}
			options = $.extend(_default,options);
			$.ajax(options);
		},
		// 获取url中的参数
		fGetQuery: function (key, urlString) {
            var reg = new RegExp('(^|&)' + key + '=([^&]*)(&|$)', 'i');
            var r;
            if (urlString != undefined && urlString != "") {
                r = urlString.substr(urlString.indexOf("?") + 1).match(reg);
            } else {
                r = window.location.search.substr(1).match(reg);
            }

            if (r != null) {
                return decodeURIComponent(r[2]);
            }
            return null;
		},
		// 十位数补充
		fFormatDecade: function(n){
			return (+n < 10 ? '0' + n : n);
		},
		// 日期格式话
		fFormatDate: function(date){
			if (/^\d{8}$/.test(date)) {
				return date.substring(0, 4) + '-' + date.substring(4, 6) + '-' + date.substring(6, 8);
			}
			date = date ? new Date(date) : new Date();
			return date != 'Invalid Date' ? date.getFullYear() + '-' + CommFunc.fFormatDecade(date.getMonth() + 1) + '-' + CommFunc.fFormatDecade(date.getDate()) : 0;
		},
		//检查微信版本，微信5.0版本后才有支付功能
		fCheckWeChat: function(){
			var NowVersionInfo = navigator.userAgent.match(/MicroMessenger\/([\d\.]+)/i);
			var NowVersion = NowVersionInfo[1];
			var MinVersion = "6.3.23";
			// var MinVersion = "5.0";
			if (NowVersion >= MinVersion) {
				return true;
			} else {
				return false;
			}
		},
		//初始化微信jssdk配置
		fWXInit: function(jsAPIList){
			this.fAjax({
				url : this.baseUrl + "weixin/config",
                type : 'get',
                data : {
					openid: CommFunc.fGetQuery("openid"),
					url: location.href
                },
                success : function(data) {
                    var wxConfig= {
						debug: false, // 开启调试模式,调用的所有api的返回值会在客户端alert出来，若要查看传入的参数，可以在pc端打开，参数信息会通过log打出，仅在pc端时才会打印。
						appId: data.appId, // 必填，企业号的唯一标识，此处填写企业号corpid
						timestamp: data.timeStamp, // 必填，生成签名的时间戳
						nonceStr: data.nonceStr, // 必填，生成签名的随机串
						signature: data.signature,// 必填，签名，见附录1
						jsApiList:  jsAPIList// 必填，需要使用的JS接口列表，所有JS接口列表见附录2
					}
					wx.config(wxConfig);
                }
			})
		},
		fWXReady: function(fn){
			wx.ready(fn);
		}
    };
    return comm;
})($);

